using System.IO.Pipes;
using System.Net.Http;
using System.Runtime.InteropServices;
using System.Security.Principal;
using System.Text;
using System.Text.Json;
using System.Windows;
using System.Windows.Threading;
using FoxyBrowser716.ErrorHandling;

namespace FoxyBrowser716;

public partial class App : Application
{
    private static Mutex? _mutex;
    
    [DllImport("kernel32.dll", SetLastError = true)]
    [return: MarshalAs(UnmanagedType.Bool)]
    private static extern bool AllocConsole();
    
    protected override void OnStartup(StartupEventArgs e)
    {
        // you see nothing here:
		if (new WindowsPrincipal(WindowsIdentity.GetCurrent()).IsInRole(WindowsBuiltInRole.Administrator))
			Task.Run(async () => { AllocConsole(); Console.OutputEncoding = Encoding.UTF8; Console.BackgroundColor = ConsoleColor.Black; Console.ForegroundColor = ConsoleColor.DarkCyan; Console.Title = "MikuBrowser01"; Console.WriteLine(Encoding.UTF8.GetString(Convert.FromBase64String(""))); await Task.Delay(500); Console.WriteLine(InfoGetter.AppName); await Task.Delay(500); await Task.Delay(500); Console.WriteLine("Lead Dev: Vast The Doge (William Herbert)"); await Task.Delay(500); Console.WriteLine("Contributors: FoxyGuy716"); await Task.Delay(500); Console.WriteLine(InfoGetter.GitHubURL); await Task.Delay(500); });
        
#if DEBUG
        _mutex = new Mutex(true, "DEBUG_FoxyBrowser716_Mutex", out var isNewInstance);
#else
        _mutex = new Mutex(true, "FoxyBrowser716_Mutex", out var isNewInstance);
#endif

        if (!isNewInstance)
        {
            SendMessage($"NewWindow|{e.Args.FirstOrDefault(s => !string.IsNullOrWhiteSpace(s))}");
            Current.Shutdown();
            return;
        }

        base.OnStartup(e);

        AppDomain.CurrentDomain.UnhandledException += CurrentDomain_UnhandledException;
        Current.DispatcherUnhandledException += Current_DispatcherUnhandledException;
        TaskScheduler.UnobservedTaskException += TaskScheduler_UnobservedTaskException;

        Current.Exit += OnApplicationExit;

        ServerManager.RunServer(e);
    }

    private void OnApplicationExit(object sender, ExitEventArgs e)
    {
        _mutex?.ReleaseMutex();
        _mutex?.Dispose();
        _mutex = null;
    }

    private void Current_DispatcherUnhandledException(object sender, DispatcherUnhandledExceptionEventArgs e)
    {
        var errorPopup = new ErrorPopup(e);
        errorPopup.ShowDialog();
        e.Handled = true;
    }

    private void CurrentDomain_UnhandledException(object sender, UnhandledExceptionEventArgs e)
    {
        if (e.ExceptionObject is not Exception ex) return;
        
        var errorPopup = new ErrorPopup(ex);
        errorPopup.ShowDialog();
    }

    private void TaskScheduler_UnobservedTaskException(object? sender, UnobservedTaskExceptionEventArgs e)
    {
        var errorPopup = new ErrorPopup(e);
        errorPopup.ShowDialog();
        e.SetObserved();
    }

    static void SendMessage(string message)
    {
#if DEBUG
        using var client = new NamedPipeClientStream(".", "DEBUG_FoxyBrowser716_Pipe", PipeDirection.Out);
#else
        using var client = new NamedPipeClientStream(".", "FoxyBrowser716_Pipe", PipeDirection.Out);
#endif
        client.Connect(1000);
        using var writer = new System.IO.StreamWriter(client);
        writer.WriteLine(message);
        writer.Flush();
    }
}